feature["» Lock Current Outfit"] = menu.add_feature("» Lock Current Outfit", "toggle", localparents["» Outfitter"].id, function(f)
	if f.on then
		local outfit_component_table = {}
		local outfit_component_texture_table = {}
		local outfit_prop_table = {}
		local outfit_prop_texture_table = {}
		for i = 0, 11 do
			outfit_component_table[i] = ped.get_ped_drawable_variation(player.get_player_ped(player.player_id()), i)
			outfit_component_texture_table[i] = ped.get_ped_texture_variation(player.get_player_ped(player.player_id()), i)
		end
		for i = 0, 2 do
			outfit_prop_table[i] = ped.get_ped_prop_index(player.get_player_ped(player.player_id()), i)
			outfit_prop_texture_table[i] = ped.get_ped_prop_texture_index(player.get_player_ped(player.player_id()), i)
		end
		while f.on do
			system.yield(1000)
			if network.is_session_started() then
				for i = 0, 11 do
					if ped.get_ped_drawable_variation(player.get_player_ped(player.player_id()), i) ~= outfit_component_table[i] then
						ped.set_ped_component_variation(player.get_player_ped(player.player_id()), i, outfit_component_table[i], outfit_component_texture_table[i], 0)
					end
				end
				for i = 0, 2 do
					if ped.get_ped_prop_index(player.get_player_ped(player.player_id()), i) ~= outfit_prop_table[i] then
						ped.set_ped_prop_index(player.get_player_ped(player.player_id()), i, outfit_prop_table[i], outfit_prop_texture_table[i], 0)
					end
				end
			end
		end
	end
end)

feature["» Prevent Helmet Removal"] = menu.add_feature("» Prevent Helmet Removal", "toggle", localparents["» Outfitter"].id, function(f)
	settings["» Prevent Helmet Removal"].Enabled = f.on
	if f.on then
		ped.set_ped_config_flag(player.get_player_ped(player.player_id()), 36, true and 1 or 0)
		while f.on do
			system.yield(1000)
			if f.on then
				ped.set_ped_config_flag(player.get_player_ped(player.player_id()), 36, true and 1 or 0)
			else
				ped.set_ped_config_flag(player.get_player_ped(player.player_id()), 36, false and 1 or 0)
			end
		end
	end
	if not f.on then
		ped.set_ped_config_flag(player.get_player_ped(player.player_id()), 36, false and 1 or 0)
	end
	settings["» Prevent Helmet Removal"].Enabled = f.on
end)

