local orbital_cannon_map_pos_y = 0

menu.add_feature("» Remote Orbital Strike", "toggle", localparents["» Gameplay Utilities"].id, function(f)
	if f.on then
		orbital_cannon_map_is_charging = false
		orbital_cannon_map_is_shooting = false
		orbital_cannon_map_time = 0
		orbital_cannon_map_zoom = 0
		orbital_cannon_map_pos_x = text_func.round(player.get_player_coords(player.player_id()).x)
		orbital_cannon_map_pos_y = text_func.round(player.get_player_coords(player.player_id()).y)
		orbital_cannon_map_scaleform = graphics.request_scaleform_movie("ORBITAL_CANNON_MAP")
    	while f.on do
			system.yield(0)
			entity.set_entity_coords_no_offset(player.get_player_ped(player.player_id()), player.get_player_coords(player.player_id()))
			entity.freeze_entity(player.get_player_ped(player.player_id()), true)
			ped.clear_ped_tasks_immediately(player.get_player_ped(player.player_id()))
			if controls.is_disabled_control_pressed(2, 77) then
				if orbital_cannon_map_pos_y >= -10000 and orbital_cannon_map_pos_y <= 10000 then
					orbital_cannon_map_pos_y = orbital_cannon_map_pos_y + 2
				else
					orbital_cannon_map_pos_y = 0
				end
			end
			if controls.is_disabled_control_pressed(2, 133) then
				if orbital_cannon_map_pos_x >= -10000 and orbital_cannon_map_pos_x <= 10000 then
					orbital_cannon_map_pos_x = orbital_cannon_map_pos_x - 2
				else
					orbital_cannon_map_pos_x = 0
				end
			end
			if controls.is_disabled_control_pressed(2, 134) then
				if orbital_cannon_map_pos_x >= -10000 and orbital_cannon_map_pos_x <= 10000 then
					orbital_cannon_map_pos_x = orbital_cannon_map_pos_x + 2
				else
					orbital_cannon_map_pos_x = 0
				end
			end
			if controls.is_disabled_control_pressed(2, 139) then
				if orbital_cannon_map_pos_y >= -10000 and orbital_cannon_map_pos_y <= 10000 then
					orbital_cannon_map_pos_y = orbital_cannon_map_pos_y - 2
				else
					orbital_cannon_map_pos_y = 0
				end
			end
			if controls.is_disabled_control_pressed(2, 15) then
				if orbital_cannon_map_zoom >= 0 and orbital_cannon_map_zoom <= 2 then
					orbital_cannon_map_zoom = orbital_cannon_map_zoom + 0.05
				else
					orbital_cannon_map_zoom = 0
				end
			end
			if controls.is_disabled_control_pressed(2, 14) then
				if orbital_cannon_map_zoom >= 0 and orbital_cannon_map_zoom <= 2 then
					orbital_cannon_map_zoom = orbital_cannon_map_zoom - 0.05
				else
					orbital_cannon_map_zoom = 0
				end
			end
        	graphics.draw_scaleform_movie_fullscreen(orbital_cannon_map_scaleform, 255, 255, 255, 255, 0)
			graphics.begin_scaleform_movie_method(orbital_cannon_map_scaleform, "MOVE_TO")
			graphics.scaleform_movie_method_add_param_int(orbital_cannon_map_pos_x)
			graphics.scaleform_movie_method_add_param_int(orbital_cannon_map_pos_y)
        	graphics.end_scaleform_movie_method(orbital_cannon_map_scaleform)
			graphics.begin_scaleform_movie_method(orbital_cannon_map_scaleform, "ZOOM_TO")
			graphics.scaleform_movie_method_add_param_float(orbital_cannon_map_zoom)
			graphics.end_scaleform_movie_method(orbital_cannon_map_scaleform)
			if controls.is_disabled_control_pressed(2, 24) then
				orbital_cannon_map_is_charging = true
			else
				orbital_cannon_map_is_charging = false
				orbital_cannon_map_time = 0
			end
			if orbital_cannon_map_is_charging then
				if not orbital_cannon_map_is_shooting then
					graphics.begin_scaleform_movie_method(orbital_cannon_map_scaleform, "START_COUNTDOWN")
					graphics.end_scaleform_movie_method(orbital_cannon_map_scaleform)
				end
				orbital_cannon_map_is_shooting = true
				orbital_cannon_map_time = orbital_cannon_map_time + 1
			else
				if orbital_cannon_map_is_shooting then
					graphics.begin_scaleform_movie_method(orbital_cannon_map_scaleform, "CANCEL_ANIMATION")
					graphics.end_scaleform_movie_method(orbital_cannon_map_scaleform)
				end
				orbital_cannon_map_is_shooting = false
				orbital_cannon_map_time = 0
			end
			if orbital_cannon_map_time >= 499 then
				local success, float = gameplay.get_ground_z(v3(orbital_cannon_map_pos_x, orbital_cannon_map_pos_y, player.get_player_coords(player.player_id()).z + 25))
				local pos_real = utilities.get_v3_of_v2(v2(orbital_cannon_map_pos_x, orbital_cannon_map_pos_y))
				if success then
					pos_real = v3(orbital_cannon_map_pos_x, orbital_cannon_map_pos_y, float + 1)
				else
					pos_real = utilities.get_v3_of_v2(v2(orbital_cannon_map_pos_x, orbital_cannon_map_pos_y))
				end
				graphics.set_next_ptfx_asset("scr_xm_orbital")
				while not graphics.has_named_ptfx_asset_loaded("scr_xm_orbital") do
					graphics.request_named_ptfx_asset("scr_xm_orbital")
					system.wait(0)
				end
				audio.play_sound_from_coord(1, "DLC_XM_Explosions_Orbital_Cannon", pos_real, 0, true, 0, false)
				fire.add_explosion(pos_real, 59, true, false, 1, player.get_player_ped(player.player_id()))
				graphics.start_networked_ptfx_non_looped_at_coord("scr_xm_orbital_blast", pos_real, v3(0, 180, 0), 1, true, true, true)
				orbital_cannon_map_time = 0
			end
   		end
	end
    if not f.on then
		orbital_cannon_map_is_charging = false
		orbital_cannon_map_is_shooting = false
		orbital_cannon_map_time = 0
		orbital_cannon_map_zoom = 0
		orbital_cannon_map_pos_x = 0
		orbital_cannon_map_pos_y = 0
		entity.freeze_entity(player.get_player_ped(player.player_id()), false)
        graphics.set_scaleform_movie_as_no_longer_needed(orbital_cannon_map_scaleform)
    end
end)

feature["» Instant Respawn"] = menu.add_feature("» Instant Respawn", "toggle", localparents["» Gameplay Utilities"].id, function(f)
	settings["» Instant Respawn"].Enabled = f.on
	if f.on then
    	while f.on do
    	    system.wait(0)
			if entity.is_entity_dead(player.get_player_ped(player.player_id())) then
				ped.resurrect_ped(player.get_player_ped(player.player_id()))
				ped.set_ped_max_health(player.get_player_ped(player.player_id()), 328)
				ped.set_ped_health(player.get_player_ped(player.player_id()), 328)
				local random_pos_in_range = player.get_player_coords(player.player_id()) + v3(math.random(-200, 200), math.random(-200, 200), -100)
				local previous_position = player.get_player_coords(player.player_id())
				if player.is_player_in_any_vehicle(player.player_id()) then
					entity.set_entity_coords_no_offset(player.get_player_vehicle(player.player_id()), random_pos_in_range)
				else
					entity.set_entity_coords_no_offset(player.get_player_ped(player.player_id()), random_pos_in_range)
				end
				local time = utils.time_ms() + 1000
				while time > utils.time_ms() do
					system.yield(0)
					if previous_position then
						local success, float = gameplay.get_ground_z(player.get_player_coords(player.player_id()))
						if not success then
							if player.is_player_in_any_vehicle(player.player_id()) then
								utilities.request_control(player.get_player_vehicle(player.player_id()))
								entity.set_entity_coords_no_offset(player.get_player_vehicle(player.player_id()), v3(player.get_player_coords(player.player_id()).x, player.get_player_coords(player.player_id()).y, player.get_player_coords(player.player_id()).z + 50))
							else
								entity.set_entity_coords_no_offset(player.get_player_ped(player.player_id()), v3(player.get_player_coords(player.player_id()).x, player.get_player_coords(player.player_id()).y, player.get_player_coords(player.player_id()).z + 50))
							end
						else
							if player.is_player_in_any_vehicle(player.player_id()) then
								utilities.request_control(player.get_player_vehicle(player.player_id()))
