menu.add_feature("» Destroy All Vehicle Doors", "action", localparents["» Door Control"].id, function(f)
	if player.is_player_in_any_vehicle(player.player_id()) then
		local all_stones = {}
		utilities.request_model(3175864630)
		for i = 1, 8 do
			vehicle.set_vehicle_door_open(player.get_player_vehicle(player.player_id()), i - 1, false, true)
		end
		system.wait(100)
		for i = 1, 8 do
			vehicle.set_vehicle_door_open(player.get_player_vehicle(player.player_id()), i - 1, false, true)
		end
		system.wait(10)
		for i = 1, 8 do
			all_stones[i] = object.create_object(3175864630, entity.get_entity_coords(player.get_player_vehicle(player.player_id())) + v3(math.random(-2, 2), math.random(-2, 2), 10), true, false)
			utilities.request_control_silent(all_stones[i])
			entity.set_entity_visible(all_stones[i], false)
			entity.apply_force_to_entity(all_stones[i], 3, 0, 0, -180, 0.0, 0.0, 0.0, false, true)
		end
		system.wait(1000)
		for i = 1, 8 do
			utilities.request_control_silent(all_stones[i])
			utilities.hard_remove_entity(all_stones[i])
		end
	else
		menu.notify("You are not in a vehicle", Meteor, 3, 211)
	end
end)

