menu.add_feature("» Save To Default", "action", localparents["» Settings"].id, function(f)
	text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", "") .. "\\Default.ini", "w"), "")
	for i = 1, #all_settings do
		text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", "") .. "\\Default.ini", "a"), "" .. all_settings[i] .. "|" .. string.format("%s", settings[all_settings[i]].Enabled) .. "|" .. string.format("%s", settings[all_settings[i]].Value) .. "\n")
	end
	system.wait(100)
	menu.notify("Successfully saved config to settings!", Meteor, 3, 0x00ff00)
end)

