local function update_settings_profiles()
	for i = 1, 50 do
		if feature["» Settings Profile " .. i] then
			menu.delete_feature(feature["» Settings Profile " .. i].id)
		end
	end
	local all_ini_profiles = utils.get_all_files_in_directory(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles", ""), "ini")
	if #all_ini_profiles > 0 then
		for i = 1, #all_ini_profiles do
			feature["» Settings Profile " .. i] = menu.add_feature("" .. all_ini_profiles[i] .. "", "action_value_str", localparents["» Settings Profiles"].id, function(f)
				if f.value == 0 then
					if utils.file_exists(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i])) then
						local all_load_profiles = utils.get_all_files_in_directory(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\Load", ""), "ini")
						for i = 1, #all_load_profiles do
							if utils.file_exists(utils.get_appdata_path("PopstarDevs", "2Take1Menu").."\\MeteorProfiles\\Load\\" .. all_load_profiles[i]) then
								io.remove(utils.get_appdata_path("PopstarDevs", "2Take1Menu").."\\MeteorProfiles\\Load\\" .. all_load_profiles[i])
							end
						end
						text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\Load\\", "") .. "\\" .. all_ini_profiles[i], "w"), "")
						menu.notify("Selected Profile now loads on default!", Meteor, 3, 0x00ff00)
					end
				elseif f.value == 1 then
					if utils.file_exists(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i])) then
						local file_real = all_ini_profiles[i]
						text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", file_real), "w"), "")
						for i = 1, #all_settings do
							text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", file_real), "a"), "" .. all_settings[i] .. "|" .. string.format("%s", settings[all_settings[i]].Enabled) .. "|" .. string.format("%s", settings[all_settings[i]].Value) .. "\n")
						end
						system.wait(100)
						menu.notify("Successfully saved config to Profile!", Meteor, 3, 0x00ff00)
					end
				elseif f.value == 2 then
					if utils.file_exists(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i])) then
						text_func.write(io.open(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i]), "w"), "")
						menu.notify("Successfully reset Profile!", Meteor, 3, 0x00ff00)
					end
				elseif f.value == 3 then
					if utils.file_exists(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i])) then
						if all_ini_profiles[i] ~= "Default.ini" then
							io.remove(utils.get_appdata_path("PopstarDevs\\2Take1Menu\\MeteorProfiles\\", all_ini_profiles[i]))
							menu.notify("Successfully deleted Profile!", Meteor, 3, 0x00ff00)
							update_settings_profiles()
						else
							menu.notify("You can't delete the default profile!", Meteor, 3, 211)
						end
					end
				end
			end)
			feature["» Settings Profile " .. i]:set_str_data({"Load As Default", "Save", "Reset", "Delete"})
		end
	end
end

