				end
			end)
		end
	end
	if not f.on then
		if eventhooks["» Log Script Events " .. pid] then
			hook.remove_script_event_hook(eventhooks["» Log Script Events " .. pid])
			eventhooks["» Log Script Events " .. pid] = nil
		end
	end
end)

