menu.add_feature("» Set Parachute Model", "action", localparents["» Player Options"].id, function(f)
	local input_stat, input_val = input.get("Enter Hash or Model", "", 99, 0)
	if input_stat == 1 then
		return HANDLER_CONTINUE
	end
	if input_stat == 2 then
		return HANDLER_POP
	end
	if math.type(tonumber(input_val)) == "integer" and streaming.is_model_valid(input_val) then
		player.set_player_parachute_model(player.player_id(), input_val)
	elseif streaming.is_model_valid(gameplay.get_hash_key(input_val)) then
		player.set_player_parachute_model(player.player_id(), gameplay.get_hash_key(input_val))
	end
end)

menu.add_feature("» Resurrect", "action", localparents["» Player Options"].id, function(f)
	if entity.is_entity_dead(player.get_player_ped(player.player_id())) then
		local vehicle_ = nil
		if player.is_player_in_any_vehicle(player.player_id()) then
			vehicle_ = player.get_player_vehicle(player.player_id())
		end
		ped.resurrect_ped(player.get_player_ped(player.player_id()))
		ped.set_ped_max_health(player.get_player_ped(player.player_id()), 328)
		ped.set_ped_health(player.get_player_ped(player.player_id()), 328)
		ped.clear_ped_tasks_immediately(player.get_player_ped(player.player_id()))
		if vehicle_ ~= nil then
			ped.set_ped_into_vehicle(player.get_player_ped(player.player_id()), vehicle_, vehicle.get_free_seat(vehicle_))
		end
	end
end)

