feature["» Auto Resurrect"] = menu.add_feature("» Auto Resurrect", "toggle", localparents["» Player Options"].id, function(f)
	settings["» Auto Resurrect"].Enabled = f.on
	if f.on then
		while f.on do
			system.yield(0)
			if entity.is_entity_dead(player.get_player_ped(player.player_id())) then
				local vehicle_ = nil
				if player.is_player_in_any_vehicle(player.player_id()) then
					vehicle_ = player.get_player_vehicle(player.player_id())
				end
				ped.resurrect_ped(player.get_player_ped(player.player_id()))
				ped.set_ped_max_health(player.get_player_ped(player.player_id()), 328)
				ped.set_ped_health(player.get_player_ped(player.player_id()), 328)
				for i = 1, 50 do
					ped.clear_ped_tasks_immediately(player.get_player_ped(player.player_id()))
					if vehicle_ ~= nil then
						ped.set_ped_into_vehicle(player.get_player_ped(player.player_id()), vehicle_, vehicle.get_free_seat(vehicle_))
					end
					system.wait(1)
				end
			end
		end
	end
	settings["» Auto Resurrect"].Enabled = f.on
end)

feature["» Demi Passive Mode"] = menu.add_feature("» Demi Passive Mode", "toggle", localparents["» Player Options"].id, function(f)
