			v3(-X, Y,  Z),
			v3(0.0, Y,  0.0),
			v3(0.0, Y,  Z),
			v3(X, Y,  0.0),
			v3(X, Y,  Z),
			v3(-X, halfY,  0.0),
			v3(-X, halfY,  Z),
			v3(0.0, halfY,  0.0),
			v3(0.0, halfY,  Z),
			v3(X, halfY,  0.0),
			v3(X, halfY,  Z),
			v3(-X, 0.0,  0.0),
