feature["» Attach Front Ramp"] = menu.add_feature("» Attach Front Ramp", "action_value_str", localparents["» Vehicle Modifications"].id, function(f)
	settings["» Attach Front Ramp"].Value = f.value
	if player.is_player_in_any_vehicle(player.player_id()) then
		network.request_control_of_entity(player.get_player_vehicle(player.player_id()))
		utilities.request_model(2934970695)
		all_front_ramps_table[all_front_ramps_count] = object.create_object(2934970695, player.get_player_coords(player.player_id()), true, false)
		if f.value == 1 then
			entity.set_entity_visible(all_front_ramps_table[all_front_ramps_count], false)
		end
		entity.attach_entity_to_entity(all_front_ramps_table[all_front_ramps_count], player.get_player_vehicle(player.player_id()), 0, v3(0, 6, 0.2), v3(0, 0, 180), true, true, false, 0, true)
		all_front_ramps_count = all_front_ramps_count + 1
	else
		menu.notify("You have to be in a vehicle!", Meteor, 4, 211)
	end
end)
feature["» Attach Front Ramp"]:set_str_data({"Visible", "Invisible"})
