menu.add_feature("» Modify Vehicle Speed", "action", localparents["» Vehicle Modifications"].id, function(f)
    if player.is_player_in_any_vehicle(player.player_id()) then
        local input_stat, input_val = input.get("Enter Modified Speed", "", 12, 3)
        if input_stat == 1 then
            return HANDLER_CONTINUE
        end
        if input_stat == 2 then
            return HANDLER_POP
        end
		utilities.request_control_silent(player.get_player_vehicle(player.player_id()))
        vehicle.modify_vehicle_top_speed(player.get_player_vehicle(player.player_id()), input_val / 3.6)
		entity.set_entity_max_speed(player.get_player_vehicle(player.player_id()), input_val / 3.6)
		vehicle.set_vehicle_engine_torque_multiplier_this_frame(player.get_player_vehicle(player.player_id()), input_val / 3.6)
    else
        menu.notify("You are not in a vehicle", Meteor, 3, 211)
    end
end)

