menu.add_player_feature("» Teleport To", "action_value_str", playerparents["» Vehicle"], function(f, pid)
	if player.is_player_in_any_vehicle(pid) then
		if f.value == 0 then
			utilities.request_control(player.get_player_vehicle(pid))
			entity.set_entity_coords_no_offset(player.get_player_vehicle(pid), utilities.offset_coords_forward(player.get_player_coords(player.player_id()), player.get_player_heading(player.player_id()), 5) + v3(0, 0, 1))
		elseif f.value == 1 then
			if ui.get_waypoint_coord().x ~= 16000 then
				utilities.request_control(player.get_player_vehicle(pid))
				local previous_position = player.get_player_coords(pid)
				local pos = v3(ui.get_waypoint_coord().x, ui.get_waypoint_coord().y, -100)
				if player.is_player_in_any_vehicle(pid) then
					entity.set_entity_coords_no_offset(player.get_player_vehicle(pid), pos)
				else
					entity.set_entity_coords_no_offset(player.get_player_ped(pid), pos)
				end
				local time = utils.time_ms() + 1000
				while time > utils.time_ms() do
					system.yield(0)
					if previous_position then
						local success, float = gameplay.get_ground_z(player.get_player_coords(pid))
						if not success then
							if player.is_player_in_any_vehicle(pid) then
								entity.set_entity_coords_no_offset(player.get_player_vehicle(pid), v3(player.get_player_coords(pid).x, player.get_player_coords(pid).y, player.get_player_coords(pid).z + 50))
							else
								entity.set_entity_coords_no_offset(player.get_player_ped(pid), v3(player.get_player_coords(pid).x, player.get_player_coords(pid).y, player.get_player_coords(pid).z + 50))
							end
						else
							if player.is_player_in_any_vehicle(pid) then
								entity.set_entity_coords_no_offset(player.get_player_vehicle(pid), v3(player.get_player_coords(pid).x, player.get_player_coords(pid).y, float + 1))
							else
								entity.set_entity_coords_no_offset(player.get_player_ped(pid), v3(player.get_player_coords(pid).x, player.get_player_coords(pid).y, float + 1))
							end
							previous_position = nil
						end
						ui.set_waypoint_off()
					end
				end
				if previous_position then
					if player.is_player_in_any_vehicle(pid) then
						entity.set_entity_coords_no_offset(player.get_player_vehicle(pid), previous_position)
					else
						entity.set_entity_coords_no_offset(player.get_player_ped(pid), previous_position)
					end
					previous_position = nil
				end
			else
				menu.notify("Waypoint not found.", Meteor, 3, 211)
			end
		end
	else
		menu.notify("Player is not in a vehicle", Meteor, 3, 211)
	end
end):set_str_data({
